mapper.select:
select c.*,r.taskcode,r.worker,r.tasktype,r.starttime,r.endtime
from reportcontent c
left join taskreportinfo r on r.id=c.reportid
where c.reportid = #{reportid} and c.reportstate ='1'

  and c.areaname =#{areaname}


  and c.equipname =#{equipname}


  and r.exceptionstate=#{exceptionstate}


  and date_format(c.operationtime, '%Y-%m-%d'
  ) between #{time1,jdbcType=VARCHAR}  
and #{time2,jdbcType=VARCHAR}
order by c.id DESC
limit #{page},#{pagesize}

select count(*)
from reportcontent c
left join taskreportinfo r on r.id=c.reportid
where c.reportid = #{reportid} and c.reportstate ='1'

  and c.areaname =#{areaname}


  and c.equipname =#{equipname}


  and r.exceptionstate=#{exceptionstate}


  and date_format(c.operationtime, '%Y-%m-%d'
  ) between #{time1,jdbcType=VARCHAR}  
and #{time2,jdbcType=VARCHAR}
order by c.id DESC
limit #{page},#{pagesize}
