mapper.select:
select t.*,s.customid sitename,u.realname username
from taskplaninfo t left join siteareainfo s on s.id=t.siteid left join userinfo u on t.directorid=u.id where t.type=#{type} and t.state>0
and t.siteid in
#{sid}
order by t.createtime DESC limit #{page},#{size}

select count(t.id) from taskplaninfo t left join userinfo u on t.directorid=u.id where t.type=#{type} and t.state>0
and t.siteid in
#{sid}

select t.*,s.customid sitename,u.realname username

from taskplaninfo t left join siteareainfo s on s.id=t.siteid
left join userinfo u on t.directorid=u.id where 1=1

  and t.identifyingid like #{identifyingid}


  and t.state=#{state}

and t.type=#{type} and t.state>0

  and t.siteid in
  
    #{sid}
  

order by t.createtime DESC
limit #{page},#{size}

select count(t.id) from taskplaninfo t
left join userinfo u on t.directorid=u.id
where 1=1

  and t.identifyingid like #{identifyingid}


  and t.state=#{state}

and t.type=#{type} and t.state>0

  and t.siteid in
  
    #{sid}
