mapper.select:
SELECT * FROM taskreportinfo WHERE
 taskcode LIKE #{taskName}
 AND DATE_FORMAT(uploadtime, '%Y-%m-%d') BETWEEN #{startTime}
    AND #{endTime}
ORDER BY taskcode DESC

select
,
from taskreportinfo where id = #{id,jdbcType=BIGINT}

select ter.id,ter.taskid,ter.donetime,ter.uploadtime,ter.reportstate,ter.worker,ter.termimalid, ter.operationstate,ter.taskcode,ter.examtime,ter.examstate,ter.examuser,u.realname,term.customid tercustomid,temp.executetime,temp.id tempid,temp.state tempstate, temp.operationstate tempoperationstate,temp.stopstate,temp.updatetime, st.id stopid,st.reason stopreason,st.content stopcontent,st.stoptime stoptime,st.classname stopclassname,st.directorname stopdirectorname,temp.taskcode temptaskcode from tasktempinfo temp left join taskreportinfo ter on ter.taskcode=temp.taskcode left join taskstopinfo st on temp.id=st.tasktempid left join taskplaninfo t on t.id=temp.taskid left join userinfo u on t.directorid=u.id left join terminalinfo term on ter.termimalid=term.id left join siteareainfo s on s.id=t.siteid where temp.type=#{tasktype} and temp.state not in(0,-1,1) and s.id in
#{siteid}
order by temp.id DESC limit #{page},#{pagesize}

select count(temp.id) from tasktempinfo temp left join taskreportinfo ter on ter.taskcode=temp.taskcode left join taskstopinfo st on temp.id=st.tasktempid left join taskplaninfo t on t.id=temp.taskid left join userinfo u on t.directorid=u.id left join terminalinfo term on ter.termimalid=term.id left join siteareainfo s on s.id=t.siteid where temp.type=#{tasktype} and temp.state not in(0,-1,1) and s.id in
#{siteid}
order by temp.id DESC

select ter.id,ter.taskid,ter.donetime,ter.uploadtime,ter.reportstate,ter.worker,ter.termimalid,
ter.operationstate,ter.taskcode,ter.examtime,ter.examstate,ter.examuser,ter.starttime,ter.endtime, u.realname,term.customid tercustomid,temp.id tempid,temp.state tempstate,
temp.operationstate tempoperationstate,temp.stopstate,temp.taskcode temptaskcode,temp.executetime,temp.updatetime,
st.id stopid,st.reason stopreason,st.content stopcontent,st.stoptime stoptime,st.classname stopclassname,st.directorname stopdirectorname
from tasktempinfo temp
left join taskreportinfo ter on ter.taskcode=temp.taskcode
left join taskstopinfo st on temp.id=st.tasktempid
left join taskplaninfo t on t.id=temp.taskid
left join userinfo u on t.directorid=u.id
left join terminalinfo term on ter.termimalid=term.id
left join siteareainfo s on s.id=t.siteid
where temp.type=#{tasktype}
and s.id in

  #{siteid}


  and t.identifyingid = #{taskcode}


  and ter.reportstate=#{reportstate}


  and  temp.operationstate=#{operationstate}


  and temp.state in
  
    #{state}



  and temp.stopstate=#{stopstate}


  and date_format(temp.executetime, '%Y-%m-%d'
  ) between #{time1,jdbcType=VARCHAR}  
and #{time2,jdbcType=VARCHAR}
order by temp.id DESC
limit #{page},#{pagesize}

