mapper.select:
select u.id,u.type,u.username,u.realname,u.sex,u.state,u.userstate,c.id,c.customid,t.customid tercustomid,p.positionname from userinfo u left join classinfo c on u.classid=c.id left join positioninfo p on p.id=u.positionid left join terminalinfo t on t.id=u.commonterminalid where 1=1
and c.siteareaid in
#{classid}
limit #{page},#{pagesize}

select count(u.id) from userinfo u left join classinfo c on u.classid=c.id where 1=1
and c.siteareaid in
#{classid}

select
u.id,u.type,u.username,u.realname,u.sex,u.state,u.userstate,c.id,c.customid,p.positionname,t.customid tercustomid,p.positionname
from userinfo u
left join classinfo c on u.classid=c.id
left join positioninfo p on p.id=u.positionid
left join terminalinfo t on t.id=u.commonterminalid
where 1=1

  and   c.siteareaid in
  
    #{classid}
  


  and u.realname like #{realname}


  and u.type=#{type}


  and u.userstate=#{userstate}

limit #{page},#{pagesize}

select count(u.id) from userinfo u
left join classinfo c on u.classid=c.id
where 1=1

  and c.siteareaid in
  
    #{classid}
  


  and u.realname like #{realname}


  and u.type=#{type}


  and u.userstate=#{userstate}

select

from userinfo
where username = #{0} AND  password = #{1}
